import { getXYCoordinates } from '../../utils';

const Star = ({ star, mapRange }) => {
  const mag = Number(star.mag);

  const { x, y } = getXYCoordinates(star.ra, star.dec);

  let r = 7.0 - mag;
  if (r < 1) {
    r = 1;
  }

  let animClass = 'slow';
  if (mag < 8) {
    animClass = 'med';
  } else if (mag < 5) {
    animClass = 'fast';
  }

  console.log('star-render', star, x, y, r);

  return (
    <circle cx={x} cy={y} r={r} fill="url(#StarGradient)" className={animClass}>
      <animate />
    </circle>
  );
};

export default Star;
