import styled from 'styled-components';

const StarMapCanvas = styled.svg`
  background-color: black;

  @keyframes buttonTransition {
    from {
      r: 20%;
    }
    to {
      r: 25%;
    }

    circle {
      animation-duration: 1s;
      animation-iteration-count: infinite;
      animation-name: buttonTransition;
    }
  }
`;

const StarMap = ({ width = 500, height = 500 }) => {
  return (
    <StarMapCanvas fill="lightGrey">
      <defs>
        <radialGradient id="StarGradient">
          <stop offset="0%" stop-color="lightGrey" />
          <stop offset="100%" stop-color="black" />
        </radialGradient>
      </defs>

      <circle cx="50" cy="50" r="40" fill="url(#StarGradient)">
        {' '}
        <animate
          attributeName="r"
          begin="0s"
          dur="1s"
          repeatCount="indefinite"
          from="20%"
          to="25%"
        />
      </circle>
      <circle cx="150" cy="50" r="4" fill="url(#StarGradient)" />
    </StarMapCanvas>
  );
};

export default StarMap;
