import React from 'react';
import Link from 'next/link';
import styled from 'styled-components';
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
import { MdBlurCircular } from 'react-icons/md';

import Header from '../styles/Header';
import NavBar from '../components/NavBar';
import { __Type } from 'graphql';

const DATA_QUERY = gql`
  query dsos($rangeInput: Object!) {
    dsos(input: $rangeInput) @client
    stars(input: $rangeInput) @client
  }
`;

const SELECTION_QUERY = gql`
  query {
    hops @client
  }
`;

const hopToGQL = hop => {
  const myHop = {
    ...hop,
    __typename: 'selectedHop'
  };

  myHop.startingLocation.__typename = 'startingLocation';
  myHop.targetLocation.__typename = 'targetLocation';

  console.log('myHop', myHop);
  return myHop;
};

const HopDiv = styled.div`
  display: grid;
  grid-template-columns: 1fr 2fr 5fr 2fr;
  margin: 10px;
  justify-items: center;
  align-items: center;
`;

const Difficulty = styled(MdBlurCircular)`
  color: ${props =>
    props.difficulty === 'easy'
      ? 'green'
      : props.difficulty === 'medium'
      ? 'yellow'
      : 'red'};
`;

const HopSelection = () => {
  const { client, data, loading } = useQuery(SELECTION_QUERY, {
    variables: { rangeInput: { raFrom: 1, raTo: 5, decFrom: 0, decTo: 50 } }
  });

  console.log('data', data);

  return (
    <div>
      <Header />
      <NavBar />
      {!loading &&
        data.hops.map(hop => (
          <HopDiv key={hop.id}>
            <span>{hop.id}</span>
            <Difficulty size={20} difficulty={hop.difficulty} />
            <span>{hop.description}</span>
            <span>
              <button
                onClick={() => {
                  client.writeData({
                    data: { selectedHop: hopToGQL(hop) }
                  });
                }}
              >
                Select
              </button>
            </span>
          </HopDiv>
        ))}
    </div>
  );
};

export default HopSelection;
