import { useEffect } from 'react';
import { useRouter } from 'next/router';

const GATag = () => {
  const router = useRouter();
  useEffect(() => {
    ReactGA.pageview(router.pathname);
  }, []);

  console.log('GA Tag for ' + router?.pathname);
  return null;
};

export default GATag;
