import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
import styled from 'styled-components';

import Star from './Star';

const StarMapCanvas = styled.svg`
  background-color: black;
`;

const DATA_QUERY = gql`
  query dsos($rangeInput: Object!) {
    dsos(input: $rangeInput) @client
    stars(input: $rangeInput) @client
  }
`;

const StarMap = ({ width = 500, height = 500 }) => {
  const { client, data, loading, refetch } = useQuery(DATA_QUERY, {
    variables: { rangeInput: { raFrom: 1, raTo: 5, decFrom: 0, decTo: 50 } }
  });

  return (
    <StarMapCanvas fill="lightGrey">
      <defs>
        <radialGradient id="StarGradient">
          <stop offset="0%" stop-color="lightGrey" />
          <stop offset="100%" stop-color="black" />
        </radialGradient>
      </defs>

      <Star star={{ ra: 0.5, dec: 10, magnitude: 3 }} />
    </StarMapCanvas>
  );
};

export default StarMap;
