const Star = ({ star, mapRange }) => {
  const cx = (Number(star.ra) - mapRange.raFrom) * 10;
  const cy = Number(star.dec) - mapRange.decTo;
  let r = 15.0 - Number(star.mag);
  if (r < 1) {
    r = 1;
  }

  console.log('star-render', star, cx, cy, r);

  return <circle cx={cx} cy={cy} r={r} fill="url(#StarGradient)" />;
};

export default Star;
