import styled from 'styled-components';

const MainDiv = styled.div`
  border-style: solid;
  border-color: blue;
  border-width: 2px;
  height: 300px;
  width: 600px;
  justify-self: center;
  align-self: center;
`;

const SelectedHop = ({ selectedHop }) => {
  if (
    !selectedHop ||
    !selectedHop.targetLocation ||
    !selectedHop.targetLocation.ra > 0
  ) {
    return null;
  }
  return (
    <MainDiv>
      <span>{selectedHop.id}</span>
    </MainDiv>
  );
};

export default SelectedHop;
