const Star = ({ star }) => {
  const cx = Number(star.ra) * 100;
  const cy = Number(star.dec) * 5;
  let r = 15.0 - Number(star.mag);
  if (r < 1) {
    r = 1;
  }

  //   console.log('star', star);
  //   console.log('params', cx, cy, r);

  return <circle cx={cx} cy={cy} r={r} fill="url(#StarGradient)" />;
};

export default Star;
