import { getXYCoordinates } from '../../utils';

const Star = ({ star, mapRange }) => {
  const mag = Number(star.mag);

  const { x, y } = getXYCoordinates(star.ra, star.dec);

  let r = 7.0 - mag;
  if (r < 1) {
    r = 1;
  }

  console.log('star-render', star, x, y, r);

  return (
    <circle cx={x} cy={y} r={r} fill="url(#StarGradient)" opacity={0.8}>
      <animate
        id="animation1"
        attributeName="opacity"
        from="0"
        to="1"
        dur="1s"
        begin="0s;animation2.end"
      />
      <animate
        id="animation2"
        attributeName="opacity"
        from="1"
        to="0"
        dur="1s"
        begin="animation1.end"
      />
    </circle>
  );
};

export default Star;
