import ApolloClient from 'apollo-boost';

const resolvers = {
  dsos: ({ raFrom, raTo, decFrom, decTo }) => {
    console.log('getting Dsos', raFrom, raTo, decFrom, decTo);
    return [];
  }
};

const defaults = {
  hopSelection: {
    __typename: 'hopSelection'
  },
  location: {
    ra: 0,
    dec: 0,
    __typename: 'location'
  }
};

export const client = new ApolloClient({
  resolvers
});

export default client;

export const reset = () => {
  client.writeData({
    data: {
      ...defaults
    }
  });
};

reset();
